import React, { Component } from 'react';
import { FormGroup, ControlLabel, FormControl, Col, Grid, Row, Button, ButtonToolbar} from 'react-bootstrap';

export default class PersonalDetailsForm extends Component {

  titles = ["Mr", "Miss", "Mrs", "Dr"];

  stateTitles = {value: null};

  handleChange = (event) => {
      this.setState({
          value: event.target.value
      });
  }

    render() {
        return (

            <div>
                <h2>Personal Details Page</h2>
                <Grid>
                    <Row className="show-grid">
                        <Col xs={2} md={2}>
                            <div>
                                Selected Value: {this.stateTitles.value}
                            </div>
                        </Col>
                        <Col xs={12} md={8}>

                            <form onSubmit={this.props.onSubmit}>
                                <FormGroup
                                    controlId="formControlsSelect">
                                    <ControlLabel>Title: </ControlLabel>
                                    <FormControl
                                        componentClass="select"
                                        data={this.titles}
                                        value={this.stateTitles.value}
                                        onChange={this.props.onChange}
                                      />
                                </FormGroup>
                                <FormGroup
                                    controlId="formBasicText">
                                    <ControlLabel>First Name: </ControlLabel>
                                    <FormControl
                                        type="text"
                                        placeholder="Enter First Name"
                                        onChange={this.props.onChange}
                                        name="firstName" />
                                </FormGroup>

                                <FormGroup
                                    controlId="formBasicText">
                                    <ControlLabel>Last Name: </ControlLabel>
                                    <FormControl
                                        type="text"
                                        placeholder="Enter Last Name"
                                        onChange={this.props.onChange}
                                        name="lastName" />
                                </FormGroup>
                                <ButtonToolbar>
                                    <Button bsStyle="primary"type="submit" value="Submit">Submit</Button>
                                </ButtonToolbar>
                            </form>

                        </Col>
                        <Col xs={2} md={2}></Col>
                    </Row>

                </Grid>

            </div>
        );
    }
}
