import React, { Component } from "react";
import "./App.css";


import Welcome from './components/Welcome';
import PersonalDetailsForm from './components/PersonalDetails';


const MAX_STEPS = 4;

class App extends Component {
    constructor(props){
        super(props);
        this.state ={
            step:0
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event){
        event.preventDefault();
        this.setState({
            step: (this.state.step ===MAX_STEPS)? 0 :this.state.step +1
        });
    }

    handleChange(event){
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    determineRender(){
        switch(this.state.step){
            case 0:
                return(<Welcome onChange={this.handleChange} onSubmit={this.handleSubmit}/>);
            case 1:
                return(<PersonalDetailsForm onChange={this.handleChange} onSubmit={this.handleSubmit}/>);
                default:
                    return <div>You skipped too much...</div>;        
        }
    }

    render(){
        return(
            <div className = "App">
                <h1>Allstate Autoquote</h1>
                {this.determineRender()}
            </div>
        );
    }
}

export default App;
